import { useState } from 'react';
import axios from 'axios';
import './ManageGroup.css';

export default function ManageGroup() {
  const [groupCode, setGroupCode] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [students, setStudents] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const handleAuthSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:8080/api/group/auth', {
        groupCode,
        password,
      });

      if (res.data.authenticated) {
        setIsAuthenticated(true);

        const studentsRes = await axios.get(`http://localhost:8080/api/student/list/${groupCode}`);
        setStudents(studentsRes.data);
        setErrorMessage('');
      } else {
        setErrorMessage('그룹 코드 또는 비밀번호가 잘못되었습니다.');
      }
    } catch (err) {
      console.error(err);
      setErrorMessage('오류가 발생했습니다. 다시 시도해주세요.');
    }
  };

  return (
    <div className="manage-group-container">
      {!isAuthenticated ? (
        <form onSubmit={handleAuthSubmit} className="auth-form">
          <h2>내가 만든 그룹 관리</h2>
          <label>그룹 코드</label>
          <input
            type="text"
            value={groupCode}
            onChange={(e) => setGroupCode(e.target.value)}
            required
          />

          <label>비밀번호</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {errorMessage && <p className="error-message">{errorMessage}</p>}

          <button type="submit">접속하기</button>
        </form>
      ) : (
        <div className="student-list-section">
          <h2>참여 학생 목록</h2>
          <ul className="student-list">
            {students.length === 0 ? (
              <li>아직 등록된 학생이 없습니다.</li>
            ) : (
              students.map((student, index) => (
                <li key={index} className="student-item">
                  <p><strong>이름:</strong> {student.studentName}</p>
                  <p><strong>전화번호:</strong> {student.phoneNumber}</p>
                  <p><strong>MBTI:</strong> {student.mbti}</p>
                  <p><strong>팀 역할:</strong> {student.role_type}</p>
                </li>
              ))
            )}
          </ul>
        </div>
      )}
    </div>
  );
}
