import { useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function TeamMaker() {
  const location = useLocation();
  const { groupCode } = location.state;

  const [groupInfo, setGroupInfo] = useState(null);
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [groupRes, studentsRes] = await Promise.all([
          axios.get(`http://localhost:8080/api/group/info/${groupCode}`),
          axios.get(`http://localhost:8080/api/student/list/${groupCode}`),
        ]);

        setGroupInfo(groupRes.data);  // course_name, total_students, students_per_team
        setStudentList(studentsRes.data);  // 모든 수강생 정보
      } catch (error) {
        console.error('데이터 불러오기 실패:', error);
      }
    };

    fetchData();
  }, [groupCode]);

  const handleTeamMaking = async () => {
    if (!groupInfo || studentList.length === 0) {
      alert('데이터가 불충분합니다.');
      return;
    }

    const requestData = {
      groupCode: groupCode,
      courseName: groupInfo.courseName,
      totalStudents: groupInfo.totalStudents,
      studentsPerTeam: groupInfo.studentsPerTeam,
      students: studentList.map((s) => ({
        studentName: s.studentName,
        mbti: s.mbti,
        role_type: s.role_type
      }))
    };

    try {
      const res = await axios.post('http://localhost:8000/cluster/make-teams', requestData); // FastAPI 주소
      console.log('팀 구성 결과:', res.data);
      // 결과가 팀 구성된 배열이면 화면에 표시하거나 다음 페이지로 넘김
    } catch (error) {
      console.error('팀 구성 요청 실패:', error);
      alert('팀 구성 중 오류가 발생했습니다.');
    }
  };


  return (
    <div>
      <h2>팀 구성 도우미</h2>
      {groupInfo && (
        <>
          <p>과목명: {groupInfo.courseName}</p>
          <p>총 인원: {groupInfo.totalStudents}</p>
          <p>팀당 인원: {groupInfo.studentsPerTeam}</p>
        </>
      )}

      <h3>학생 목록</h3>
      <ul>
        {studentList.map((s, i) => (
          <li key={i}>
            {s.studentName} - {s.mbti} - {s.role_type}
          </li>
        ))}
      </ul>

      {/* 이 데이터들을 FastAPI로 POST해 클러스터링 요청 예정 */}
    </div>
  );
}
